mapper.select:
    SELECT cie.id, cie.client_inquiry_id, cie.item, cie.part_number, e.part_number_code,
	 cie.description, cie.unit, cie.amount, cie.remark, cie.element_id, cie.update_timestamp 
	 FROM client_inquiry_element cie INNER JOIN element e ON (cie.element_id=e.id) WHERE cie.id=#{clientInquiryElementId}

SELECT sqe.id, si.client_inquiry_id FROM supplier_quote_element sqe INNER JOIN supplier_quote sq ON (sqe.supplier_quote_id = sq.id) INNER JOIN supplier_inquiry si ON ( sq.supplier_inquiry_id = si.id ) INNER JOIN client_inquiry ci ON (si.client_inquiry_id = ci.id) INNER JOIN supplier s ON (si.supplier_id = s.id) INNER JOIN supplier_inquiry_element sie ON ( sqe.supplier_inquiry_element_id = sie.id ) INNER JOIN client_inquiry_element cie ON ( sie.client_inquiry_element_id = cie.id )
WHERE sqe.supplier_quote_status_id = '70' AND sq.quote_status_id = 90 AND ( sqe.element_id =#{elementid}
) union SELECT sqe.id, si.client_inquiry_id
FROM supplier_quote_element sqe INNER JOIN supplier_quote sq ON ( sqe.supplier_quote_id = sq.id ) INNER JOIN supplier_inquiry si ON ( sq.supplier_inquiry_id = si.id ) INNER JOIN client_inquiry ci ON (si.client_inquiry_id = ci.id) INNER JOIN supplier s ON (si.supplier_id = s.id) INNER JOIN supplier_inquiry_element sie ON ( sqe.supplier_inquiry_element_id = sie.id ) INNER JOIN client_inquiry_element cie ON ( sie.client_inquiry_element_id = cie.id )
WHERE sqe.supplier_quote_status_id = '70' and sq.quote_status_id=90 AND ( cie.element_id =#{elementid} )

